mapper.select:
SELECT B.BOARD_NO AS BOARD_NO, SUBSTR( B.BOARD_CONTENT, 1, 100 ) AS BOARD_CONTENT /*100자 까지만 로딩*/, B.BOARD_SHARE AS BOARD_SHARE, B.VIEWS AS VIEWS, B.BOARD_TYPE AS BOARD_TYPE, B.BOARD_TITLE AS BOARD_TITLE, B.BOARD_LIKE AS BOARD_LIKE, TO_CHAR(B.WRITE_DATE, 'YYYY-MM-DD HH:MM:SS') AS WRITE_DATE, B.WRITER AS WRITER, ( SELECT COUNT(REPLY_NO) FROM REPLY_INFO WHERE BOARD_NO = B.BOARD_NO ) AS REPLY_COUNT FROM BOARD_INFO B WHERE 1=1 AND B.BOARD_SHARE = 'Y'
AND B.BOARD_TYPE = #{boardType}
AND B.BOARD_NO = #{boardNo}
ORDER BY B.BOARD_NO ASC

mapper.update:
UPDATE BOARD_INFO SET
    BOARD_CONTENT = #{boardContent}
    BOARD_TITLE = #{boardTitle}
    BOARD_SHARE = #{share}
    WRTIE_DATE = SYSDATE
WHERE 1=1
AND BOARD_NO = #{boardNo}
